import React, {createContext, useContext, useState, useMemo} from 'react'

const TransactionContext = createContext()

const useTransaction = () => {
  const context = useContext(TransactionContext)
  if (!context) {
    throw new Error(`useTransaction must be used within a CountProvider`)
  }
  return context
}


const TransactionProvider = (props) => {
  const [transaction, setTransaction] = useState([])
  const value = useMemo(() => [transaction, setTransaction], [transaction])
  return <TransactionContext.Provider value={value} {...props} />
}

export {TransactionProvider, useTransaction}
