        <li className={transaction.amount < 0 ? 'minus' : 'plus'}>
            {transaction.text} <span>{transaction.amount > 0 && sign}{(transaction.amount)} â‚¬</span> <span onClick={() => deleteTransaction(transaction.id)} className="delete-btn">X</span>
